   $content  = curl_exec($ch);            curl_close($ch);            return $content;        }	?>    <table class="form-table">    <?php        $base_url = 'http://docker-compose-ui:5000/api/v1/';        $response = get_web_page($base_url . "projects");        $resArr = array();        $resArr = json_decode($response, 1);        // d($resArr);        $active_icon = '<span class="dashicons dashicons-image-rotate" style="color: green;"></span> &nbsp;';            // echo '<pre>' . print_r($resArr['active'][1]) . '</pre>';        foreach ($resArr['projects'] as $key => $value) {            if($value) {                // $response_details = get_web_page("http://docker-compose-ui:5000/api/v1/projects/" . $elements[0]);                // $detArr = array();                // $detArr = json_decode($response_details, 1);                $cleaned_proj = strtolower(preg_replace("/[^A-Za-z0-9]/", '', $key));                $active = array_search($cleaned_proj, $resArr['active']);                // echo '<pre>' . $elements[0] . '</pre>';                // echo '<hr>';                echo '<tr valign="top">';                echo '<th scope="row">' . ($active !== false ? $active_icon : '') . $key .'</th>';                echo '<td><input type="button" name="' . $cleaned_proj . '_view" id="' . $cleaned_proj . '_view" value="View YML" /></td>';                echo '</tr>';            }        }    ?>    </table>    <p class="submit">    <input type="submit" class="button-primary" value="<?php _e('Save Changes') ?>" />    </p></form></div>